<template>
  <div class="container mt-5">
    <div class="row justify-content-center">
      <div class="col-md-8">
        <div class="card shadow-lg p-4">
          <div class="card-body text-center">
            <h2 class="mb-4">Información del perfil</h2>
            <div class="mb-3">
              <img src="https://via.placeholder.com/150" class="rounded-circle img-thumbnail" alt="Profile Picture"/>
            </div>
            <p><strong>Nombre:</strong> {{ profileData.name }}</p>
            <p><strong>Fecha de Nacimiento:</strong> {{ profileData.birthdate }}</p>
            <p><strong>Género:</strong> {{ profileData.gender }}</p>
            <p><strong>Descripción:</strong> {{ profileData.description }}</p>
            <router-link :to="`/profile/${username}/edit-account`" class="btn btn-primary w-100">
              Editar perfil
            </router-link>
          </div>
        </div>
      </div>
    </div>
  </div>
</template>

<script>
import apiClient from '@/services/ApiService';

export default {
  data() {
    return {
      profileData: {
        name: '',
        email: '',
        birthdate: '',
        gender: '',
        description: ''
      },
      username: this.$route.params.username // Obtener el nombre de usuario desde la URL
    }
  },
  async mounted() {
    console.log("Username:", this.username); 
    this.profileData = response.data.msg;
  },
  methods: {
    async updateProfile() {
      const token = localStorage.getItem('authToken');
      if (token) {
        try {
          await apiClient.put('/profile', this.profileData, {
            headers: {
              Authorization: `Bearer ${token}`
            }
          });
          alert('Perfil actualizado exitosamente');
        } catch (error) {
          console.error('Error updating profile:', error);
        }
      }
    }
  }
};
</script>
