FRONT-PROFILE_COMPONENT

<template>
  <div class="profile-container container my-4 py-4">
    <div class="profile-header row align-items-center">
      <div class="col-md-3 text-center">
        <img :src="getProfileImage()" class="profile-image img-fluid rounded-circle border" alt="Profile Picture" />
      </div>
      <div class="col-md-9 profile-info">
        <h1 class="h3">{{ profileData.name }}</h1>
        <p class="text-muted"><strong>{{ profileData.username || 'No disponible' }}</strong></p>
        <div class="profile-stats d-flex justify-content-between my-3">
          <span><strong>{{ publications }}</strong> publicaciones</span>
          <span><strong>{{ followers }}</strong> seguidores</span>
          <span><strong>{{ following }}</strong> seguidos</span>
        </div>
        <p v-if="profileData.description">{{ profileData.description }}</p>
        <router-link v-if="isCurrentUser" :to="`/profile/${profileData.username}/edit-account`"
          class="btn btn-primary btn-block">
          Editar perfil
        </router-link>
      </div>
    </div>

    <!-- <div class="profile-bio mt-4">
      <p>Blog personal: <span>{{ profileData.blog || 'No disponible' }}</span></p>
    </div> -->

    <!-- Botón para agregar nueva publicación -->
    <div v-if="isCurrentUser"
      class="add-post-button my-4 text-center d-flex justify-content-center align-items-baseline">
      <button @click="addNewPost" class="btn btn-success rounded-circle">
        <i class="fas fa-plus"></i>
      </button>
      <div class="px-2">
        <h3> AGREGAR NUEVO POST</h3>
      </div>
    </div>

    <div v-for="post in posts.filter(post => post.username === this.profileData.username)" :key="post.id" class="card mb-4 shadow-sm">
      <div class="card-body">
        <h2 class="card-title">{{ post.title }}</h2>
        <p class="text-muted small mb-2">subido por {{ post.username }}</p>
        <p class="card-text">{{ post.description }}</p>
        <img v-if="post.images" :src="post.images" alt="Post Image"
          class="img-fluid rounded my-3" />
        <div class="d-flex justify-content-between align-items-center">
          <a v-if="post.pdf_file" :href="post.images" target="_blank" class="btn btn-primary">
            📄 Ver libro (PDF)
          </a>
          <button @click="likePost(post.id)" class="btn btn-success">
            👍 Like ( {{ post.likes_count || 0 }} )
          </button>
        </div>
      </div>
    </div>

  </div>

</template>

<script>
import apiClient from '@/services/ApiService';

export default {
  data() {
    return {
      profileData: {
        name: '',
        birthdate: '',
        gender: '',
        description: '',
        profile_picture: ''
      },
      isCurrentUser: false,
      publications: 0,
      followers: 0,
      following: 0,
      posts: []
    };
  },
  computed: {
    formattedBirthdate() {
      if (this.profileData.birthdate) {
        const date = new Date(this.profileData.birthdate);
        return date.toLocaleDateString();
      }
      return '';
    },
    filteredPosts() {
      return this.posts.filter(post => post.username === this.profileData.username);
    }
  },
  watch: {
    '$route'() {
      this.getProfile();
      this.getAllPosts();
    }
  },
  methods: {
    async getProfile() {
      const token = localStorage.getItem('authToken');
      if (token) {
        try {
          const response = await apiClient.get(`/profile/${this.$route.params.username}`, {
            headers: {
              Authorization: `Bearer ${token}`
            }
          });
          this.profileData = response.data.user;
          this.isCurrentUser = response.data.isCurrentUser;
          if (this.isCurrentUser) localStorage.setItem('profileData', JSON.stringify(this.profileData));
        } catch (error) {
          console.error("Error fetching profile:", error);
        }
      }
    },
    getProfileImage() {
      const staticFiles = "http://localhost:4000/static/profiles/";
      return this.profileData.profile_picture
        ? staticFiles + this.profileData.profile_picture
        : staticFiles + "default.jpg";
    },
    async getAllPosts() {
      try {
        const token = localStorage.getItem('authToken');
        const response = await apiClient.get('/posts/user', {
          headers: {
            Authorization: `Bearer ${token}`
          }
        }
        )
        this.posts = response.data;
        console.log(this.posts);
      } catch (error) {
        console.error('Error getting post:', error);
      }
    },
    // Método para manejar la creación de una nueva publicación
    addNewPost() {
      this.$router.push('/posts');
    }
  },
  mounted() {
    this.getProfile();
    this.getAllPosts();
  }
};
</script>

<style scoped>
.profile-container {
  background-color: rgba(255, 255, 255, 0.9);
  /* Blanco con 90% de opacidad */
  border-radius: 8px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
}

.profile-header {
  display: flex;
}

.profile-image {
  width: 150px;
  height: 150px;
}

.profile-info h1 {
  font-size: 1.75rem;
}

.profile-stats {
  font-size: 1rem;
}

.profile-bio {
  border-top: 1px solid #ddd;
  padding-top: 15px;
}

.add-post-button .btn {
  font-size: 1.5rem;
  width: 50px;
  height: 50px;
  display: inline-flex;
  align-items: center;
  justify-content: center;
}

.btn {
  margin-top: 10px;
}

@media (max-width: 768px) {
  .profile-header {
    flex-direction: column;
    text-align: center;
  }

  .profile-image {
    margin-bottom: 20px;
  }

  .profile-info {
    text-align: center;
  }

  .profile-stats {
    flex-direction: column;
    gap: 10px;
  }
}
</style>
