<template>
  <div class="profile-info">
    <h2>Información del perfil</h2>
    <form @submit.prevent="updateProfile">
      <label for="name">Nombre:</label>
      <input type="text" v-model="profileData.name" />

      <label for="email">Email:</label>
      <input type="email" v-model="profileData.email" disabled />

      <label for="birthdate">Fecha de Nacimiento:</label>
      <input type="date" v-model="profileData.birthdate" />

      <label for="gender">Género:</label>
      <select v-model="profileData.gender">
        <option value="masculino">Masculino</option>
        <option value="femenino">Femenino</option>
      </select>

      <label for="description">Descripción:</label>
      <textarea v-model="profileData.description"></textarea>

      <button type="submit">Guardar Cambios</button>
    </form>
  </div>
</template>

<script>
import apiClient from '@/services/ApiService';

export default {
  data() {
    return {
      profileData: {
        name: '',
        email: '',
        birthdate: '',
        gender: '',
        description: ''
      }
    };
  },
  async mounted() {
    const token = localStorage.getItem('authToken');
    if (token) {
      try {
        const response = await apiClient.get('/profile', {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });
        this.profileData = response.data.msg;
      } catch (error) {
        console.error("Error fetching profile:", error);
      }
    }
  },
  methods: {
    async updateProfile() {
      const token = localStorage.getItem('authToken');
      if (token) {
        try {
          await apiClient.put('/profile', this.profileData, {
            headers: {
              Authorization: `Bearer ${token}`
            }
          });
          alert('Perfil actualizado exitosamente');
        } catch (error) {
          console.error('Error updating profile:', error);
        }
      }
    }
  }
};
</script>
