<template>
  <div class="sidebar">
    <ul>
      <li @click="navigateToHome">
        <i class="fa fa-home"></i> Inicio
      </li>
      <li @click="navigateToProfile">
        <i class="fa fa-user"></i> Perfil
      </li>
      <li @click="navigateToSearch">
        <i class="fa fa-search"></i> Buscar
      </li>
      <li @click="logout">
        <i class="fa fa-sign-out"></i> Cerrar sesión
      </li>
    </ul>
  </div>
</template>

<script>
export default {
  data() {
    return {
      profileData: JSON.parse(localStorage.getItem('profileData')) || {
        name: '',
        username: '',
        profile_picture: ''
      }
    };
  },
  methods: {
    navigateToHome() {
      this.$router.push('/');
    },
    navigateToProfile() {
      const isAuthenticated = this.checkAuthentication(); 
      if (isAuthenticated) {
        this.$router.push(`/profile/${this.profileData.username}`); // Asegúrate de que `username` esté en `profileData`
      } else {
        alert("Por favor, inicia sesión primero.");
      }
    },
    navigateToSearch() {
      this.$router.push('/search'); 
    },
    // logout() {
    //   localStorage.removeItem('authToken');
    //   localStorage.removeItem('profileData'); // Limpiar datos de perfil
    //   this.$router.push('/login'); 
    // },
    checkAuthentication() {
      return !!localStorage.getItem('authToken');
    }
  }
}
</script>

<style scoped>
.sidebar {
  width: 200px;
  background-color: #f8f9fa; /* Color de fondo */
  padding: 20px;
  position: fixed;
  height: 100%;
}

.profile {
  display: flex;
  align-items: center;
  margin-bottom: 20px;
}

.profile-picture {
  width: 40px;
  height: 40px;
  border-radius: 50%;
  margin-right: 10px;
}

.username {
  font-weight: bold;
}

.sidebar ul {
  list-style: none;
  padding: 0;
}

.sidebar li {
  padding: 10px;
  cursor: pointer;
}

.sidebar li:hover {
  background-color: #e9ecef; /* Color de fondo al pasar el ratón */
}
</style>
