import axios from "axios";

const apiClient = axios.create({
  baseURL: "http://localhost:4000/api", // Cambia esto si tu servidor está en otra URL
  headers: {
    "Content-Type": "application/json",
  },
});

export default {
  post(url, data) {
    return apiClient.post(url, data);
  },
  // Otros métodos como GET, PUT, DELETE si los necesitas...
};
