import apiClient from "@/services/ApiService";
export default {
  props: {
    userId: {
      type: String,
      required: true
    }
  },
  data() {
    return {
      isFollowing: null,
      // Indica si el usuario ya está siguiendo al otro usuario
      followerCount: 0,
      // Cantidad de seguidores del usuario
      followingCount: 0
    };
  },
  async created() {
    try {
      await this.fetchFollowerCount(); // Obtén la cantidad de seguidores
      await this.fetchFollowingCount();
      await this.checkIfFollowing(); // Verifica si el usuario actual ya está siguiendo al otro usuario
      console.log("userId recibido en FollowComponent:", this.userId); // Debug
    } catch (error) {
      console.error("Error durante la inicialización:", error);
    }
  },
  methods: {
    // Obtiene el contador de seguidores
    async fetchFollowerCount() {
      const token = localStorage.getItem("authToken");
      if (!token) {
        console.error("Token de autenticación no encontrado");
        return;
      }
      try {
        const response = await apiClient.get(`/follow/followers/count/${this.userId}`, {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });
        this.followerCount = response.data.followerCount;
      } catch (error) {
        console.error("Error al obtener la cantidad de seguidores:", error);
      }
    },
    // Obtiene el contador de personas seguidas
    async fetchFollowingCount() {
      const token = localStorage.getItem("authToken");
      if (!token) {
        console.error("Token de autenticación no encontrado");
        return;
      }
      try {
        const response = await apiClient.get(`/follow/following/count/${this.userId}`, {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });
        this.followingCount = response.data.followingCount;
      } catch (error) {
        console.error("Error al obtener la cantidad de seguidos:", error);
      }
    },
    // Cambia el estado de seguir/no seguir
    async toggleFollow() {
      const token = localStorage.getItem("authToken");
      if (!token) {
        console.error("Token de autenticación no encontrado");
        return;
      }
      if (!this.userId) {
        console.error("ID de usuario no definido");
        return;
      }
      try {
        if (this.isFollowing) {
          // Dejar de seguir
          await apiClient.delete(`/follow/${this.userId}`, {
            headers: {
              Authorization: `Bearer ${token}`
            },
            data: {
              followed_id: this.userId
            }
          });
          this.followerCount--;
          this.isFollowing = false;
        } else {
          // Seguir
          const payload = {
            followed_id: this.userId
          };
          console.log("Enviando datos:", payload); // Debug para verificar los datos enviados
          await apiClient.post(`/follow`, payload, {
            headers: {
              Authorization: `Bearer ${token}`
            }
          });
          this.followerCount++;
          this.isFollowing = true;
        }
        this.$emit("update-followers", this.followerCount); // Emite el evento con el nuevo conteo
      } catch (error) {
        console.error("Error al cambiar el estado de seguimiento:", error);
        if (error.response && error.response.data) {
          console.error("Detalles del error:", error.response.data);
        }
      }
    },
    // Verifica si el usuario ya está siguiendo
    async checkIfFollowing() {
      const token = localStorage.getItem("authToken");
      if (!token) {
        console.error("Token de autenticación no encontrado");
        return;
      }
      try {
        const response = await apiClient.get(`/follow/check/${this.userId}`, {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });
        this.isFollowing = response.data.isFollowing;
      } catch (error) {
        console.error("Error al verificar el estado de seguimiento:", error);
      }
    }
  }
};
