<template>
  <div class="container mt-5">
    <div class="row justify-content-center">
      <div class="col-md-8">
        <div class="card shadow-lg p-4">
          <div class="card-body text-center">
            <h2 class="mb-4">Información del perfil</h2>
            <div class="mb-3">
              <img src="https://via.placeholder.com/150" class="rounded-circle img-thumbnail" alt="Profile Picture"/>
            </div>
            <form @submit.prevent="updateProfile">
              <div class="form-group mb-3">
                <label for="name">Nombre</label>
                <input type="text" id="name" v-model="profileData.name" class="form-control"/>
              </div>
              <div class="form-group mb-3">
                <label for="dob">Fecha de Nacimiento</label>
                <input type="date" id="dob" v-model="profileData.dob" class="form-control"/>
              </div>
              <div class="form-group mb-3">
                <label for="gender">Género</label>
                <select id="gender" v-model="profileData.gender" class="form-control">
                  <option value="masculino">Masculino</option>
                  <option value="femenino">Femenino</option>
                </select>
              </div>
              <div class="form-group mb-4">
                <label for="description">Descripción</label>
                <textarea id="description" v-model="profileData.description" class="form-control" rows="3"></textarea>
              </div>
              <button type="submit" class="btn btn-primary w-100">
                Guardar cambios
              </button>
            </form>
          </div>
        </div>
      </div>
    </div>
  </div>
</template>

<script>
import apiClient from '@/services/ApiService';

export default {
  data() {
    return {
      profileData: {
        name: '',
        dob: '',
        gender: '',
        description: ''
      }
    };
  },
  async mounted() {
    const token = localStorage.getItem('authToken');
    if (token) {
      try {
        const response = await apiClient.get('/profile', {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });
        this.profileData = response.data.msg;
      } catch (error) {
        console.error("Error fetching profile:", error);
      }
    }
  },
  methods: {
    async updateProfile() {
      const token = localStorage.getItem('authToken');
      if (token) {
        try {
          const response = await apiClient.put('/profile', this.profileData, {
            headers: {
              Authorization: `Bearer ${token}`
            }
          });
          console.log('Profile updated successfully:', response.data);
        } catch (error) {
          console.error('Error updating profile:', error);
        }
      }
    }
  }
};
</script>
