import { createRouter, createWebHistory } from 'vue-router';
import HomeView from "../views/home_view.vue";
import LoginView from '../views/login_view.vue';
import RegisterView from '../views/register_view.vue';
import ForgotPassword from "../views/forgotpassword_view.vue";
import ResetPassword from "../views/resetpassword_view.vue";
import ProfileView from "../views/profile_view.vue";
import EditProfileView from "../views/editprofile_view.vue";
import PostView from "../views/post_view.vue";
import EditPostView from "../views/editpost_view.vue";
import LikeView from "../views/like_view.vue";
import PdfPreviewView from "../views/pdfpreview_view.vue";
import DashboardView from '../views/admin/dashboard_view.vue';
import AdminLayout from '../layouts/AdminLayout.vue'; // Layout para rutas de administrador
import MainLayout from '@/layouts/MainLayout.vue'; // Layout para rutas principales

const routes = [
  {
    path: '/',
    component: MainLayout, // Layout principal
    children: [
      {
        path: '',
        name: 'Home',
        component: HomeView,
        meta: { requiresAuth: true }, // Esta ruta requiere autenticación
      },
      {
        path: '/profile/:username',
        name: 'Profile',
        component: ProfileView,
        meta: { requiresAuth: true }, // Esta ruta requiere autenticación
        props: true, // Permite pasar `username` como prop al componente
      },
      {
        path: '/profile/:username/edit-account',
        name: 'EditProfile',
        component: EditProfileView,
        props: true,
        meta: { requiresAuth: true }, // Esta ruta requiere autenticación
      },
      {
        path: '/posts',
        name: 'PostView',
        component: PostView,
        meta: { requiresAuth: true }, // Esta ruta requiere autenticación
      },
      {
        path: '/posts/:postId/edit-post',
        name: 'EditPost',
        component: EditPostView,
        props: true,
        meta: { requiresAuth: true }, // Esta ruta requiere autenticación
      },
      {
        path: '/like',
        name: 'LikeView',
        component: LikeView,
        meta: { requiresAuth: true }, // Esta ruta requiere autenticación
      },
      {
        path: '/pdf-preview',
        name: 'PdfPreview',
        component: PdfPreviewView,
        meta: { requiresAuth: true }, // Esta ruta requiere autenticación
      },
    ],
  },
  {
    path: '/login',
    name: 'Login',
    component: LoginView,
  },
  {
    path: '/register',
    name: 'Register',
    component: RegisterView,
  },
  {
    path: '/forgot-password',
    name: 'ForgotPassword',
    component: ForgotPassword,
  },
  {
    path: '/reset-password',
    name: 'ResetPassword',
    component: ResetPassword,
  },
  {
    path: '/admin',
    component: AdminLayout, // Layout exclusivo para rutas de administrador
    meta: { requiresAuth: true }, // Esta ruta requiere autenticación
    children: [
      {
        path: 'dashboard',
        name: 'AdminDashboard',
        component: DashboardView,
      },
    ],
  },
];

const router = createRouter({
  history: createWebHistory(),
  routes,
});

// Guard de navegación global
router.beforeEach((to, from, next) => {
  const isAuthenticated = !!localStorage.getItem('authToken'); // Verifica si hay un token

  if (to.meta.requiresAuth && !isAuthenticated) {
    // Si la ruta requiere autenticación y no está autenticado, redirige al login
    next({ path: '/login' });
  } else {
    // De lo contrario, permite el acceso
    next();
  }
});

export default router;
